mapper.select:
SELECT A.RNUM , A.NOTICE_NO , A.NOTICE_TITLE , A.REG_DATE FROM (SELECT ROWNUM RNUM , B.NOTICE_NO , B.NOTICE_TITLE , B.REG_DATE FROM (SELECT C.NOTICE_NO , C.NOTICE_TITLE , C.REG_DATE FROM COM_NOTICE C
C.NOTICE_TITLE LIKE '%' || #{ searchValue } || '%'
ORDER BY C.REG_DATE DESC ) B
WHERE ROWNUM <= #{ endRow }
) A WHERE A.RNUM >= #{ startRow }

mapper.update:
UPDATE 
	   COM_NOTICE CN
   SET CN.NOTICE_TITLE = #{ noticeTitle }
     , CN.NOTICE_CONTENT = #{ noticeContent }
 WHERE CN.NOTICE_NO = #{ noticeNo }

mapper.delete:
DELETE
	   COM_NOTICE CN
 WHERE CN.NOTICE_NO = #{ noticeNo }
