mapper.select:
SELECT A.RNUM , A.COM_NO , A.COM_NAME , A.MEMBER_ID , A.CATEGORY , A.COM_ADDRESS , A.COM_STATUS FROM (SELECT ROWNUM RNUM , B.COM_NO , B.COM_NAME , B.MEMBER_ID , B.CATEGORY , B.COM_ADDRESS , B.COM_STATUS FROM (SELECT C.COM_NO , C.COM_NAME , C.MEMBER_ID , C.CATEGORY , C.COM_ADDRESS , D.COM_STATUS FROM COM_INFO C JOIN COM_RECORD D ON(C.COM_NO = D.COM_NO)
C.MEMBER_ID = #{ searchValue }
C.COM_NAME LIKE '%' || #{ searchValue } || '%'
D.COM_STATUS LIKE '%' || #{ searchValue } || '%'
AND C.COM_NO IS NOT NULL
ORDER BY D.COM_NO DESC ) B
WHERE ROWNUM <= #{ endRow }
)A WHERE A.RNUM >= #{ startRow }

mapper.update:
	UPDATE
		COM_RECORD A
	SET COM_STATUS = '승인'
  WHERE A.COM_NO = #{ comNo }
    AND A.RECORD_NO = #{ recordNo }

mapper.delete:
DELETE
	FROM COM_INFO A
   WHERE A.COM_NO = #{ comNo }

mapper.update:
	UPDATE
		COM_RECORD A
	SET COM_STATUS = '거절'
	  , REFUSE_REASON = #{ reason }
  WHERE A.COM_NO = #{ comNo }
    AND A.RECORD_NO = #{ recordNo }

	UPDATE
		COM_RECORD A
	
		
		A.COM_STATUS = #{ updateStatus },
		
		
		A.REFUSE_REASON = #{ reason },
		
	
  WHERE A.COM_NO = #{ comNo }
    AND A.RECORD_NO = #{ recordNo }

mapper.select:
SELECT A.RNUM , A.COM_NO , A.COM_NAME , A.WORK_TIME , A.HOLIDAY , E.THUMBNAIL_NAME FROM(SELECT ROWNUM RNUM , B.COM_NO , B.COM_NAME , B.WORK_TIME , B.HOLIDAY FROM(SELECT C.COM_NO , C.COM_NAME , C.WORK_TIME , C.HOLIDAY FROM COM_INFO C JOIN COM_RECORD D ON(C.COM_NO = D.COM_NO)
C.CATEGORY = #{ searchCondition }
C.COM_ADDRESS LIKE '%' || #{ searchValue } || '%'
AND D.COM_STATUS = '승인'
) B
WHERE ROWNUM <= #{ endRow }
) A JOIN COM_BOARD_ATTACH E ON(A.COM_NO = E.COM_NO) WHERE A.RNUM >= #{ startRow }

SELECT A.RNUM , A.COM_NO , A.COM_NAME , A.WORK_TIME , A.HOLIDAY , A.THUMBNAIL_NAME FROM(SELECT ROWNUM RNUM , B.COM_NO , B.COM_NAME , B.WORK_TIME , B.HOLIDAY , B.THUMBNAIL_NAME FROM(SELECT V.COM_NO , V.COM_NAME , V.WORK_TIME , V.HOLIDAY , V.THUMBNAIL_NAME
FROM (SELECT K.COM_NO , K.COM_NAME , K.WORK_TIME , K.HOLIDAY , U.THUMBNAIL_NAME , T.RATINGS FROM COM_INFO K JOIN COM_BOARD_ATTACH U ON(K.COM_NO = U.COM_NO) JOIN REVIEW T ON(K.COM_NO = T.COM_NO) JOIN COM_RECORD D ON(K.COM_NO = D.COM_NO) WHERE D.COM_STATUS = '승인' ) V GROUP BY V.COM_NO , V.COM_NAME , V.WORK_TIME , V.HOLIDAY , V.THUMBNAIL_NAME ORDER BY ROUND(AVG(V.RATINGS),1)DESC) B
WHERE ROWNUM <= #{ endRow }
) A WHERE A.RNUM >= #{ startRow }
