mapper.select:
SELECT A.RNUM , A.COM_NO , A.COM_NAME , A.MEMBER_ID , A.CATEGORY , A.COM_ADDRESS , A.COM_STATUS FROM (SELECT ROWNUM RNUM , B.COM_NO , B.COM_NAME , B.MEMBER_ID , B.CATEGORY , B.COM_ADDRESS , B.COM_STATUS FROM (SELECT C.COM_NO , C.COM_NAME , C.MEMBER_ID , C.CATEGORY , C.COM_ADDRESS , D.COM_STATUS FROM COM_INFO C JOIN COM_RECORD D ON(C.COM_NO = D.COM_NO)
C.MEMBER_ID = #{ searchValue }
C.COM_NAME LIKE '%' || #{ searchValue } || '%'
D.COM_STATUS LIKE '%' || #{ searchValue } || '%'
AND C.COM_NO IS NOT NULL
ORDER BY D.COM_NO DESC ) B
WHERE ROWNUM <= #{ endRow }
)A WHERE A.RNUM >= #{ startRow }

mapper.update:
	UPDATE
		COM_RECORD A
	SET COM_STATUS = '승인'
  WHERE A.COM_NO = #{ comNo }
    AND A.RECORD_NO = #{ recordNo }

mapper.delete:
DELETE
	FROM COM_INFO A
   WHERE A.COM_NO = #{ comNo }

mapper.update:
	UPDATE
		COM_RECORD A
	SET COM_STATUS = '거절'
	  , REFUSE_REASON = #{ reason }
  WHERE A.COM_NO = #{ comNo }
    AND A.RECORD_NO = #{ recordNo }
