mapper.select:
SELECT A.RNUM , A.NOTICE_NO , A.NOTICE_TITLE , A.NOTICE_CONTENT , A.REG_DATE , A.MEMBER_ID FROM (SELECT ROWNUM RNUM , B.NOTICE_NO , B.NOTICE_TITLE , B.NOTICE_CONTENT , B.REG_DATE , B.MEMBER_ID FROM(SELECT C.NOTICE_NO , C.NOTICE_TITLE , C.NOTICE_CONTENT , C.REG_DATE , C.MEMBER_ID FROM ADMIN_NOTICE C
C.NOTICE_TITLE LIKE '%' || #{ searchValue } || '%'
ORDER BY C.REG_DATE DESC )B
WHERE ROWNUM <= #{ endRow }
) A
WHERE A.RNUM >= #{ startRow }

SELECT A.RNUM , A.NOTICE_NO , A.NOTICE_TITLE , A.REG_DATE , A.MEMBER_ID FROM(SELECT ROWNUM RNUM , B.NOTICE_NO , B.NOTICE_TITLE , B.REG_DATE , B.MEMBER_ID FROM(SELECT C.NOTICE_NO , C.NOTICE_TITLE , C.REG_DATE , C.MEMBER_ID FROM ADMIN_NOTICE C
C.MEMBER_ID LIKE '%' || #{ searchValue } || '%'
C.NOTICE_TITLE LIKE '%' || #{ searchValue } || '%'
ORDER BY C.NOTICE_NO DESC ) B
WHERE ROWNUM <= #{ endRow }
)A WHERE A.RNUM >= #{ startRow }

mapper.delete:
DELETE
	FROM ADMIN_NOTICE A
   WHERE A.NOTICE_NO = #{ noticeNo }

mapper.insert:
INSERT
	INTO ADMIN_NOTICE A
	(
		A.NOTICE_NO
	  , A.NOTICE_TITLE
	  , A.NOTICE_CONTENT
	  , A.REG_DATE
	  , A.MEMBER_ID
	)
	VALUES
	(
		SEQ_ADMINNOTICENO.NEXTVAL
	  , #{ title }
	  , #{ content }
	  , #{ date }
	  , #{ id }
	)
