mapper.select:
SELECT A.RNUM , A.REPORT_NO , A.REPORT_TITLE , A.MEMBER_ID FROM (SELECT ROWNUM RNUM , B.REPORT_NO , B.REPORT_TITLE , B.MEMBER_ID FROM(SELECT C.REPORT_NO , C.REPORT_TITLE , C.MEMBER_ID FROM REPORT C WHERE C.MEMBER_ID = #{ memId }
AND C.MEMBER_ID LIKE '%' || #{ searchValue } || '%'
AND C.REPORT_TITLE LIKE '%' || #{ searchValue } || '%'
ORDER BY C.REPORT_NO DESC ) B
WHERE ROWNUM <= #{ selectCriteria.endRow }
)A
WHERE A.RNUM >= #{ selectCriteria.startRow }

mapper.insert:
INSERT
	INTO REPORT A
	(
		A.REPORT_NO
	   ,A.REPORT_TITLE
	   ,A.REPORT_CONTENT
	   ,A.MEMBER_ID
	   ,A.REG_DATE	
	  
	)
	
VALUES 
(  
  SEQ_REPORT.NEXTVAL
  #{ title }
  #{ content }
  #{ id }
  #{ date } 
    )

mapper.delete:
DELETE
	FROM REPORT A
   WHERE A.REPORT_NO = #{ reportNo }

mapper.update:
UPDATE
		REPORT A

WHERE A.REPORT_NO = #{ reportNo }
