mapper.select:
SELECT A.RNUM , A.REVIEW_NO , A.COM_NAME , A.MEMBER_ID , A.RATINGS FROM(SELECT ROWNUM RNUM , B.REVIEW_NO , B.COM_NAME , B.MEMBER_ID , B.RATINGS FROM(SELECT C.REVIEW_NO , D.COM_NAME , F.MEMBER_ID , C.RATINGS FROM REVIEW C JOIN COM_INFO D ON(C.COM_NO = D.COM_NO) JOIN WAITING_RECORD F ON(C.RECORD_NO = F.RECORD_NO)
D.COM_NAME LIKE '%' || #{ searchValue } || '%'
F.MEMBER_ID LIKE '%' || #{ searchValue } || '%'
ORDER BY C.REVIEW_NO DESC ) B
WHERE ROWNUM <= #{ endRow }
)A WHERE A.RNUM >= #{ startRow }

mapper.delete:
DELETE
	FROM REVIEW A
   WHERE A.REVIEW_NO = #{ reviewNo }

mapper.insert:
  INSERT
    INTO REVIEW A
    (
    	A.REVIEW_NO
   	  , A.REVIEW_CONTENT
   	  , A.RATINGS
   	  , A.RECORD_NO
   	  , A.COM_NO  	
    ) 
    VALUES
    (
       SEQ_REVIEW_NO.NEXTVAL
     , #{ reviewContent }
     , #{ ratings }
     , #{ recordNo }
     , #{ comNo }  
    )
    
    
			SELECT
					SEQ_REVIEW_NO.CURRVAL
			  FROM DUAL
