mapper.select:
SELECT A.RNUM , A.FAQ_NO , A.FAQ_TITLE , A.MEMBER_ID , A.REG_DATE FROM (SELECT ROWNUM RNUM , B.FAQ_NO , B.FAQ_TITLE , B.MEMBER_ID , B.REG_DATE FROM(SELECT C.FAQ_NO , C.FAQ_TITLE , C.MEMBER_ID , C.REG_DATE FROM TBL_FAQ C
C.MEMBER_ID LIKE '%' || #{ searchValue } || '%'
C.FAQ_TITLE LIKE '%' || #{ searchValue } || '%'
ORDER BY C.FAQ_NO DESC ) B
WHERE ROWNUM <= #{ endRow }
)A WHERE A.RNUM >= #{ startRow }

mapper.delete:
DELETE
	FROM TBL_FAQ A
   WHERE A.FAQ_NO = #{ faqNo }
