mapper.select:
SELECT A.RNUM , A.MEMBER_NUM , A.USE_DATE , A.PHONE , A.COM_NAME , A.REVIEW_NO FROM(SELECT ROWNUM RNUM , B.MEMBER_NUM , B.USE_DATE , B.PHONE , B.COM_NAME , B.REVIEW_NO FROM(SELECT C.MEMBER_NUM , C.USE_DATE , D.PHONE , E.COM_NAME , R.REVIEW_NO FROM WAITING_RECORD C JOIN MEMBER_INFO D ON(C.MEMBER_ID = D.MEMBER_ID) JOIN COM_INFO E ON(C.COM_NO = E.COM_NO) LEFT JOIN REVIEW R ON (C.RECORD_NO = R.RECORD_NO) WHERE C.MEMBER_ID = #{ searchValue } AND C.USE_STATUS = 'complete' ) B
WHERE ROWNUM <= #{ endRow }
) A
WHERE A.RNUM >= #{ startRow }

SELECT A.RNUM , A.MEMBER_NUM , A.USE_DATE , A.PHONE , A.COM_NAME FROM(SELECT ROWNUM RNUM , B.MEMBER_NUM , B.USE_DATE , B.PHONE , B.COM_NAME FROM(SELECT C.MEMBER_NUM , C.USE_DATE , D.PHONE , E.COM_NAME FROM WAITING_RECORD C JOIN MEMBER_INFO D ON(C.MEMBER_ID = D.MEMBER_ID) JOIN COM_INFO E ON(C.COM_NO = E.COM_NO) WHERE C.MEMBER_ID = #{ searchValue } AND C.USE_STATUS = 'cancel' ) B
WHERE ROWNUM <= #{ endRow }
) A
WHERE A.RNUM >= #{ startRow }

mapper.insert:
INSERT
  INTO WAITING_RECORD WR
(
	WR.RECORD_NO, WR.COM_NO, WR.MEMBER_ID
	, WR.USE_STATUS, WR.MEMBER_NUM, WR.REQUIREMENT
)
VALUES
(
	SEQ_RECORDNO.NEXTVAL, #{ comNo }, #{ userId }
	, 'waiting', #{ memberNum }, #{ requirement }
)
